mapper.update:
UPDATE tbl_board SET
board_video_id = #{boardVideoId},
board_title = #{boardTitle}, board_content = #{boardContent}, board_update_date = current_timestamp WHERE user_num = #{userNum} AND board_num = #{boardNum}

mapper.delete:
DELETE FROM tbl_board
WHERE user_num = #{userNum} AND
board_num = #{boardNum}
